import React, { useEffect, useState } from "react";
import AddProduct from "./components/AddProduct";
import ProductList from "./components/ProductList";
import SearchBar from "./components/SearchBar";
import { fetchProducts, searchProducts } from "./api";

const App = () => {
  const [products, setProducts] = useState([]);

  const loadProducts = async () => {
    const data = await fetchProducts();
    setProducts(data);
  };

  const handleSearch = async (query) => {
    if (!query) {
      loadProducts();
    } else {
      const data = await searchProducts(query);
      setProducts(data);
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold text-center mb-4">Product Catalogue</h1>
      <SearchBar onSearch={handleSearch} />
      <AddProduct onProductAdded={loadProducts} />
      <ProductList products={products} onDelete={loadProducts} />
    </div>
  );
};

export default App; // ✅ Make sure this is a default export
