import React, { useState, useEffect } from "react";
import { fetchProducts } from "./api";
import ProductList from "./components/ProductList";
import SearchBar from "./components/SearchBar";
import AddProduct from "./components/AddProduct";

const App = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    const allProducts = await fetchProducts();
    setProducts(allProducts);
    setFilteredProducts(allProducts);
  };

  const handleSearchResults = (results) => {
    setFilteredProducts(results.length ? results : products);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Product Catalogue</h1>
      <AddProduct onProductAdded={loadProducts} />
      <SearchBar onSearchResults={handleSearchResults} />
      <ProductList products={filteredProducts} onDelete={loadProducts} />
    </div>
  );
};

export default App;
