import React, { useState, useEffect, useRef } from "react";

const SearchBar = ({ onResults = () => {}, resetResults = () => {} }) => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const searchTimeout = useRef(null);

  useEffect(() => {
    if (!query) {
      resetResults();  // Reload all products when cleared
      return;
    }

    if (searchTimeout.current) {
      clearTimeout(searchTimeout.current);
    }

    searchTimeout.current = setTimeout(() => {
      axios
        .get(`http://localhost:5000/products/search?q=${query}`)
        .then((response) => {
          setResults(response.data);
          onResults(response.data);
        })
        .catch((error) => console.error("Error fetching search results:", error));
    }, 300);

    return () => clearTimeout(searchTimeout.current);
  }, [query, onResults, resetResults]);

  return (
    <div className="relative">
      <input
        type="text"
        placeholder="Search products..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="search-input border p-2 rounded w-full"
      />
      {results.length > 0 && (
        <ul className="absolute bg-white border rounded w-full mt-1 shadow-lg">
          {results.map((product) => (
            <li key={product.id} className="p-2 hover:bg-gray-200">
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
