import React, { useState, useEffect } from "react";
import ProductList from "./components/ProductList";
import AddProduct from "./components/AddProduct";
import SearchBar from "./components/SearchBar";
import { fetchProducts, searchProducts } from "./api";

const App = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    setLoading(true);
    const data = await fetchProducts();
    setProducts(data);
    setSearchResults(data); // Ensure search updates
    setLoading(false);
  };

  const handleSearch = async (query) => {
    if (query.trim() === "") {
      setSearchResults(products); // Reset search results to full product list
    } else {
      const results = await searchProducts(query);
      setSearchResults(results.length ? results : []);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-center">Product Catalogue</h1>
      <SearchBar onSearch={handleSearch} />
      <AddProduct onProductAdded={loadProducts} />
      {loading ? (
        <p className="text-center text-gray-600 mt-4">Loading...</p>
      ) : (
        <ProductList products={searchResults} onDelete={loadProducts} />
      )}
    </div>
  );
};

export default App;
