import React, { useState, useEffect } from "react";
import ProductList from "./components/ProductList";
import AddProduct from "./components/AddProduct";
import SearchBar from "./components/SearchBar";
import { fetchProducts, searchProducts } from "./api";

const App = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch products on load
  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    setLoading(true);
    const data = await fetchProducts();
    setProducts(data);
    setLoading(false);
  };

  const handleSearch = async (query) => {
    if (query.trim() === "") {
      loadProducts(); // If empty search, reload all products
    } else {
      const results = await searchProducts(query);
      setProducts(results);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Product Catalogue</h1>
      <SearchBar onSearch={handleSearch} />
      <AddProduct onProductAdded={loadProducts} />
      {loading ? <p>Loading...</p> : <ProductList products={products} onDelete={loadProducts} />}
    </div>
  );
};

export default App;
